INSERT INTO `Feedbacks` (`id`, `name`, `avatar`, `address`, `content`, `status`, `createdAt`, `updatedAt`, `deletedAt`, `age`) VALUES
(13, 'Nhật Hạ', '1650984761766-customer_3.jpg', 'Đà Nẵng', 'Sau khi đặt lịch khám bệnh tại hệ thống DoctorCare,tôi cảm thấy rất hài lòng về dịch vụ,dịch vụ rất tuyệt vời.', 1, '2022-04-26 14:52:41', '2022-04-26 14:55:43', NULL, 28),
(14, 'Mỹ Chi', '1650984860501-customer_1.jpg', 'Quảng Nam', 'Website thật tuyệt vời trên cả mong đợi,tôi đã nhanh chóng khỏi bệnh sau khi sử dụng dịch vụ khám bệnh.', 1, '2022-04-26 14:54:20', '2022-04-26 14:55:45', NULL, 25),
(15, 'Hồng Hân', '1650984935503-customer_2.jpg', 'Huế', 'Sau khi sử dụng dịch vụ trên website,tôi cảm thấy dịch vụ rất tuyệt vời,nó giúp tôi tiết kiệm thời gian trong việc khám bệnh của mình.', 1, '2022-04-26 14:55:35', '2022-04-26 14:55:52', NULL, 23);
